WINDOW FUNCTIONS:

Running total of standard_qty orderd by occurred_at and account_id
*/

SELECT occurred_at,
       account_id,
       standard_qty,
       SUM(standard_qty) OVER (ORDER BY occurred_at, account_id) AS running_total
FROM orders;

